import {useState, useEffect} from 'react'
import EmployeeForm from "./EmployeeForm";
import EmployeesList from "./EmployeesList";
import NewEmployee from './NewEmployee'
import SearchEmployee from './SearchEmployee';
import config from "../config.js"

const API = config.api;

function EmployeesScreen(props) {

    const [employees, setEmployees] = useState([]);
    const [showForm, setShowForm] = useState(false);

  //Functions
  useEffect(() => {
    const getAllEmployees = () => {
      fetch(API, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          setEmployees(data);
        });
    };
    getAllEmployees();
  }, []);
    
    const viewForm = () => {setShowForm((prevState) => !prevState)}

    const getEmployee = (term) => {
        fetch(API + "/?email=" + term, {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
            },
          })
            .then((response) => {
              return response.json();
            })
            .then((data) => {
              setEmployees([data]);
            });
    }
    const getAll = () => {
        fetch(API, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        })
          .then((response) => {
            return response.json();
          })
          .then((data) => {
            setEmployees(data);
          });
      };
      
    const updateEmployee = (emp) => {
      console.log("here");
      console.log(employees);
      fetch(API, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(emp)
      }).then(() => {
        console.log("here1");
        console.log(emp);
        
        // console.log("here2");
        
        // console.log("here3");
        // console.log(copy);
        
        setEmployees((prevState) => {
          var copy =  JSON.parse(JSON.stringify(prevState));
              copy.forEach((employee) => {
                if(employee.id === emp.id){
                  employee.f_name = emp.f_name;
                  employee.l_name = emp.l_name;
                  employee.email = emp.email;
              } 
            });
        return JSON.parse(JSON.stringify(copy))});
      })
  };

    return (
        <div>
            {showForm ? <EmployeeForm submitBtnName={"Add Employee"} viewForm={viewForm} getAll={getAll}/>: <NewEmployee viewForm={viewForm}/>}  
            <SearchEmployee submitFunc={getEmployee} getAll={getAll}></SearchEmployee>           
            <EmployeesList employees = {employees}  updateEmployee={updateEmployee}/>
        </div>
    )
}

export default EmployeesScreen;
