import {useState, useEffect} from 'react'
import EmployeeForm from "./EmployeeForm";
import EmployeesList from "./EmployeesList";
import NewEmployee from './NewEmployee'
import SearchEmployee from './SearchEmployee';
import config from "../config.js"

const API = config.api;

function EmployeesScreen(props) {

    const [employees, setEmployees] = useState([]);
    const [showForm, setShowForm] = useState(false);

  //Functions
  useEffect(() => {
    const getAllEmployees = () => {
      fetch(API, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((response) => {
          return response.json();
        })
        .then((data) => {
          setEmployees(data);
        });
    };
    getAllEmployees();
  }, []);
    
    const viewForm = () => {setShowForm((prevState) => !prevState)}
    const getEmployee = (term) => {
        console.log("here" + term)
        fetch(API + "/?email=" + term, {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
            },
          })
            .then((response) => {
              return response.json();
            })
            .then((data) => {
              console.log("here");
              console.log(data);
              setEmployees([data]);
            });
    }
    const getAll = () => {
        fetch(API, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        })
          .then((response) => {
            return response.json();
          })
          .then((data) => {
            setEmployees(data);
          });
      };
      
const setSpecific =  (emp) => {
        setEmployees((prevState) => {
          prevState.forEach((employee) => {
            if(employee.id === emp.id){
              employee.f_name = emp.f_name;
              employee.l_name = emp.l_name;
              employee.email = emp.email;
          } 
        });
        
        return [...employees]
      }).then((newState) => {setEmployees(newState)});
    
    }

    const updateEmployee = (emp) => {
      fetch(API, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(emp)
      })
        .then((emp) => {
          return setSpecific(emp);
        })
        // .then((newState) => {
        //   console.log(newState)
        //   setEmployees(newState);
        // })
        
  }

    return (
        <div>
            {showForm ? <EmployeeForm submitBtnName={"Add Employee"} viewForm={viewForm} getAll={getAll}/>: <NewEmployee viewForm={viewForm}/>}  
            <SearchEmployee submitFunc={getEmployee} getAll={getAll}></SearchEmployee>           
            <EmployeesList employees = {employees} setSpecific={setSpecific} updateEmployee={updateEmployee}/>
        </div>
    )
}

export default EmployeesScreen;
