import {useState, useEffect} from 'react'
import EmployeeForm from "./EmployeeForm";
import EmployeesList from "./EmployeesList";
import NewEmployee from './NewEmployee'
import SearchEmployee from './SearchEmployee';
import config from "../config.js"

const API = config.api;

function EmployeesScreen(props) {

    const [employees, setEmployees] = useState([]);
    const [showForm, setShowForm] = useState(false);

  //Functions
//   useEffect(() => {
//     const getAllEmployees = () => {
//       fetch(API, {
//         method: "GET",
//         headers: {
//           "Content-Type": "application/json",
//         },
//       })
//         .then((response) => {
//           return response.json();
//         })
//         .then((data) => {
//           setEmployees(data);
//         });
//     };
//     getAllEmployees();
//   }, []);
    
    const viewForm = () => {setShowForm((prevState) => !prevState)}
    const getEmployee = (term) => {
        fetch(API + new URLSearchParams({email: term}), {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
            },
          })
            .then((response) => {
              return response.json();
            })
            .then((data) => {
              setEmployees(data);
            });
    }
    return (
        <div>
            {showForm ? <EmployeeForm submitBtnName={"Add Employee"} viewForm={viewForm}/>: <NewEmployee viewForm={viewForm}/>}  
            <SearchEmployee submitFunc={getEmployee}></SearchEmployee>           
            <EmployeesList employees = {employees}/>
        </div>
    )
}

export default EmployeesScreen;
